GlobalChangeSpec.CreateProcedureSQL:
create procedure dbo.USP_GLOBALCHANGE_CATALOGREVENUEPACKSCUSTOM (
@CHANGEAGENTID uniqueidentifier = null, @ASOF as datetime = null, @NUMBERADDED int = 0 output, @NUMBEREDITED int = 0 output, @NUMBERDELETED int = 0 output, @KITCODE varchar(30) = '', @CATALOGREVENUESELECTIONID UNIQUEIDENTIFIER, @ITEM_1 varchar(30) = '', @QUANT_1 int = 0, @ITEM_2 varchar(30) = '', @QUANT_2 int = 0, @ITEM_3 varchar(30) = '', @QUANT_3 int = 0, @ITEM_4 varchar(30) = '', @QUANT_4 int = 0, @ITEM_5 varchar(30) = '', @QUANT_5 int = 0, @ITEM_6 varchar(30) = '', @QUANT_6 int = 0, @ITEM_7 varchar(30) = '', @QUANT_7 int = 0, @ITEM_8 varchar(30) = '', @QUANT_8 int = 0, @ITEM_9 varchar(30) = '', @QUANT_9 int = 0, @ITEM_10 varchar(30) = '', @QUANT_10 int = 0, @ITEM_11 varchar(30) = '', @QUANT_11 int = 0, @ITEM_12 varchar(30) = '', @QUANT_12 int = 0, @ITEM_13 varchar(30) = '', @QUANT_13 int = 0, @ITEM_14 varchar(30) = '', @QUANT_14 int = 0, @ITEM_15 varchar(30) = '', @QUANT_15 int = 0, @ITEM_16 varchar(30) = '', @QUANT_16 int = 0, @ITEM_17 varchar(30) = '', @QUANT_17 int = 0, @ITEM_18 varchar(30) = '', @QUANT_18 int = 0, @ITEM_19 varchar(30) = '', @QUANT_19 int = 0, @ITEM_20 varchar(30) = '', @QUANT_20 int = 0, @ITEM_21 varchar(30) = '', @QUANT_21 int = 0, @ITEM_22 varchar(30) = '', @QUANT_22 int = 0, @ITEM_23 varchar(30) = '', @QUANT_23 int = 0, @ITEM_24 varchar(30) = '', @QUANT_24 int = 0, @ITEM_25 varchar(30) = '', @QUANT_25 int = 0, @ITEM_26 varchar(30) = '', @QUANT_26 int = 0, @ITEM_27 varchar(30) = '', @QUANT_27 int = 0, @ITEM_28 varchar(30) = '', @QUANT_28 int = 0, @ITEM_29 varchar(30) = '', @QUANT_29 int = 0, @ITEM_30 varchar(30) = '', @QUANT_30 int = 0, @ITEM_31 varchar(30) = '', @QUANT_31 int = 0, @ITEM_32 varchar(30) = '', @QUANT_32 int = 0, @ITEM_33 varchar(30) = '', @QUANT_33 int = 0, @ITEM_34 varchar(30) = '', @QUANT_34 int = 0, @ITEM_35 varchar(30) = '', @QUANT_35 int = 0, @ITEM_36 varchar(30) = '', @QUANT_36 int = 0, @ITEM_37 varchar(30) = '', @QUANT_37 int = 0, @ITEM_38 varchar(30) = '', @QUANT_38 int = 0, @ITEM_39 varchar(30) = '', @QUANT_39 int = 0, @ITEM_40 varchar(30) = '', @QUANT_40 int = 0, @ITEM_41 varchar(30) = '', @QUANT_41 int = 0, @ITEM_42 varchar(30) = '', @QUANT_42 int = 0, @ITEM_43 varchar(30) = '', @QUANT_43 int = 0, @ITEM_44 varchar(30) = '', @QUANT_44 int = 0, @ITEM_45 varchar(30) = '', @QUANT_45 int = 0, @ITEM_46 varchar(30) = '', @QUANT_46 int = 0, @ITEM_47 varchar(30) = '', @QUANT_47 int = 0, @ITEM_48 varchar(30) = '', @QUANT_48 int = 0, @ITEM_49 varchar(30) = '', @QUANT_49 int = 0, @ITEM_50 varchar(30) = '', @QUANT_50 int = 0 ) as set nocount off;
declare @I_MAX integer declare @CURRENTDATE datetime set @CURRENTDATE = getdate(); set @NUMBERADDED = 0; set @NUMBEREDITED = 0; set @NUMBERDELETED = 0;
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output;
begin try -- do the global change operation here, and set @NUMBERADDED, @NUMBEREDITED, and/or @NUMBERDELETED as appropriate DECLARE @CATALOGREVENUESELECTION TABLE (id UNIQUEIDENTIFIER PRIMARY KEY);
INSERT INTO @CATALOGREVENUESELECTION SELECT id FROM dbo.Ufn_idsetreader_getresults_guid(@CATALOGREVENUESELECTIONID)
declare @T table ( PK int identity(1,1), CATALOGREVENUEID uniqueidentifier, CATALOGCODE nvarchar(50), INITIALQUANTITY integer, CHANGEAGENTID uniqueidentifier, CHANGEDATE datetime ) declare @I int = 1
-- insert records that meet criteria (catalog item = '69.RH2011KIT' at this point) insert into @T select CR.ID, CIA.CATALOGCODE, CR.QUANTITY, CR.CHANGEDBYID, CR.DATECHANGED from dbo.USR_CATALOGREVENUE CR inner join dbo.USR_CATALOGITEMATTRIBUTE CIA on cia.id = cr.catalogitemattributeid inner join dbo.REVENUE R on R.id = cr.revenueid inner join @CATALOGREVENUESELECTION SelectionResults on SelectionResults.ID = r.ID where cia.catalogcode = @KITCODE -- and not processed and CR.QUANTITY <> 0 and CR.FULFILLED = 0
select @I_MAX = count(*) from @T
declare @CATALOGREVENUEID uniqueidentifier declare @CATALOGCODE nvarchar(50) declare @INITIALQUANTITY integer declare @CATALOGREVENUECHANGEAGENTID uniqueidentifier declare @CHANGEDATE datetime
--BEGIN LOOPING THROUGH TABLE DEFINED ABOVE while @I <= @I_MAX begin select @CATALOGREVENUEID = CATALOGREVENUEID, @CATALOGCODE = CATALOGCODE, @INITIALQUANTITY = INITIALQUANTITY, @CATALOGREVENUECHANGEAGENTID = CHANGEAGENTID, @CHANGEDATE = CHANGEDATE from @T where PK = @I ---------------------------------------------------------------------------------------------------------------------------- -- create new CATALOGREVENUE records with new codes and correct quantities ---------------------------------------------------------------------------------------------------------------------------- begin begin try ------------------------------------------- -- Add Item 1 ------------------------------------------- insert into USR_CATALOGREVENUE ( TRANSACTIONID, CATALOGITEMATTRIBUTEID, QUANTITY, PRICE, FAIRMARKET, ADDEDBYID, CHANGEDBYID, DATEADDED, DATECHANGED, --TS, --TSLONG, FULFILLED, REVENUEID, ADDRESSID, SHIPTONAME, TAXAMOUNT, CATALOGITEMID, CATALOGTYPEID, FROMNAME, SHIPTOFIRSTNAME, SHIPTOLASTNAME, SHIPTOADDRESS1, SHIPTOADDRESS2, SHIPTOCITY, SHIPTOSTATEID, SHIPTOCOUNTRYID, SHIPTOZIP, SHIPPINGOPTION, SHIPPINGPRICE, SHIPPINGATTENTION, SHIPPINGINSTRUCTIONS, PERSONALNOTE, SHIPPINGPRICEACTUAL, SHIPDATE, ISSHIPPED, TRACKINGNUMBER, FULFILLMENTNUMBER ) select TRANSACTIONID, (select id from usr_catalogitemattribute where catalogcode = @ITEM_1), @INITIALQUANTITY * @QUANT_1, PRICE, FAIRMARKET, ADDEDBYID, CHANGEDBYID, DATEADDED, DATECHANGED, --TS, --TSLONG, FULFILLED, REVENUEID, ADDRESSID, SHIPTONAME, TAXAMOUNT, CATALOGITEMID, CATALOGTYPEID, FROMNAME, SHIPTOFIRSTNAME, SHIPTOLASTNAME, SHIPTOADDRESS1, SHIPTOADDRESS2, SHIPTOCITY, SHIPTOSTATEID, SHIPTOCOUNTRYID, SHIPTOZIP, SHIPPINGOPTION, SHIPPINGPRICE, SHIPPINGATTENTION, SHIPPINGINSTRUCTIONS, PERSONALNOTE, SHIPPINGPRICEACTUAL, SHIPDATE, ISSHIPPED, TRACKINGNUMBER, FULFILLMENTNUMBER from USR_CATALOGREVENUE where id = @CATALOGREVENUEID
-- ONLY DO THIS ON FIRST ITEM, WHICH IS REQUIRED set @NUMBEREDITED = @NUMBEREDITED + @@ROWCOUNT; ------------------------------------------- -- Add Item 2 ------------------------------------------- if @ITEM_2 <> '' begin insert into USR_CATALOGREVENUE ( TRANSACTIONID, CATALOGITEMATTRIBUTEID, QUANTITY, PRICE, FAIRMARKET, ADDEDBYID, CHANGEDBYID, DATEADDED, DATECHANGED, --TS, --TSLONG, FULFILLED, REVENUEID, ADDRESSID, SHIPTONAME, TAXAMOUNT, CATALOGITEMID, CATALOGTYPEID, FROMNAME, SHIPTOFIRSTNAME, SHIPTOLASTNAME, SHIPTOADDRESS1, SHIPTOADDRESS2, SHIPTOCITY, SHIPTOSTATEID, SHIPTOCOUNTRYID, SHIPTOZIP, SHIPPINGOPTION, SHIPPINGPRICE, SHIPPINGATTENTION, SHIPPINGINSTRUCTIONS, PERSONALNOTE, SHIPPINGPRICEACTUAL, SHIPDATE, ISSHIPPED, TRACKINGNUMBER, FULFILLMENTNUMBER ) select TRANSACTIONID, (select id from usr_catalogitemattribute where catalogcode = @ITEM_2), @INITIALQUANTITY * @QUANT_2, PRICE, FAIRMARKET, ADDEDBYID, CHANGEDBYID, DATEADDED, DATECHANGED, --TS, --TSLONG, FULFILLED, REVENUEID, ADDRESSID, SHIPTONAME, TAXAMOUNT, CATALOGITEMID, CATALOGTYPEID, FROMNAME, SHIPTOFIRSTNAME, SHIPTOLASTNAME, SHIPTOADDRESS1, SHIPTOADDRESS2, SHIPTOCITY, SHIPTOSTATEID, SHIPTOCOUNTRYID, SHIPTOZIP, SHIPPINGOPTION, SHIPPINGPRICE, SHIPPINGATTENTION, SHIPPINGINSTRUCTIONS, PERSONALNOTE, SHIPPINGPRICEACTUAL, SHIPDATE, ISSHIPPED, TRACKINGNUMBER, FULFILLMENTNUMBER from USR_CATALOGREVENUE where id = @CATALOGREVENUEID end ------------------------------------------- -- Add Item 3 ------------------------------------------- if @ITEM_3 <> '' begin insert into USR_CATALOGREVENUE ( TRANSACTIONID, CATALOGITEMATTRIBUTEID, QUANTITY, PRICE, FAIRMARKET, ADDEDBYID, CHANGEDBYID, DATEADDED, DATECHANGED, --TS, --TSLONG, FULFILLED, REVENUEID, ADDRESSID, SHIPTONAME, TAXAMOUNT, CATALOGITEMID, CATALOGTYPEID, FROMNAME, SHIPTOFIRSTNAME, SHIPTOLASTNAME, SHIPTOADDRESS1, SHIPTOADDRESS2, SHIPTOCITY, SHIPTOSTATEID, SHIPTOCOUNTRYID, SHIPTOZIP, SHIPPINGOPTION, SHIPPINGPRICE, SHIPPINGATTENTION, SHIPPINGINSTRUCTIONS, PERSONALNOTE, S