GlobalChangeSpec.CreateProcedureSQL:

create procedure dbo.USR_USP_GLOBALCHANGE_MERKLE_FILENAME_ADDCOUNT_ZIP
(
	@CHANGEAGENTID uniqueidentifier = null,
	@ASOF as datetime = null,
	@NUMBERADDED int = 0 output,
	@NUMBEREDITED int = 0 output,
	@NUMBERDELETED int = 0 output,
	@PATTERN nvarchar(20) = null
)
as			
	set nocount off;

	declare @CURRENTDATE datetime
	set @CURRENTDATE = getdate();
	set @NUMBERADDED = 0;
	set @NUMBEREDITED = 0;
	set @NUMBERDELETED = 0; 

  declare @NUMPROCESSED int = 0;

  set @NUMPROCESSED = 
    (
    select top 1 numberprocessed from businessprocessstatus  
    where businessprocesscatalogid=(select id from businessprocesscatalog where name ='Export Process')
    and businessprocessparametersetid =(select id from exportprocess where name = 'FEED - Merkle - Daily CD Link Export')
    and statuscode=0
    order by endedon desc
    )

	if @CHANGEAGENTID is null
		exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output;

	begin try
		-- do the global change operation here, and set @NUMBERADDED, @NUMBEREDITED, and/or @NUMBERDELETED as appropriate
		update usr_zipprocess
    set filename = replace(filename,@PATTERN,@NUMPROCESSED), destinationpath =  replace(destinationpath,@PATTERN,@NUMPROCESSED)
    where id = (select id from usr_zipprocess where name = 'FEED - Merkle - Daily CD Link Export')

    set @NUMBEREDITED = @@rowcount

	end try

	begin catch
		exec dbo.USP_RAISE_ERROR;
		return 1;
	end catch
